# -*- coding : utf-8 -*- #
import base64
from fontTools.ttLib import TTFont
import xml.etree.ElementTree as et
__author__ = "Gallen_qiu"
'''这个用来做匹配解密字典，打开页面，把base64字符串复制过来，然后根据页面显示的真实数字和密文去匹配'''

base64_str = ''

def gouzao():
        base64_str_decode = base64.b64decode(base64_str)
        filr_name = "验证.ttf"
        with open(filr_name, 'wb') as f:
            f.write(base64_str_decode)
        font = TTFont('验证.ttf')  # 打开本地的ttf文件
        font.saveXML('验证.xml')  # 转换成xml
font_dict={}

def make_parseDict():
    root = et.parse('验证.xml').getroot()
    gp = root.find("glyf").findall("TTGlyph")
    g_dict = {}
    for c in gp:
        try:
            g_dict[c.attrib['name']] = int(c.attrib['xMin']) + int(c.attrib['yMin']) + int(c.attrib['xMax']) + int(
                c.attrib['yMax']) - 2650
        except:
            pass

    cmap = root.find('cmap').find('cmap_format_4').findall('map')

    for c in cmap:
        try:
            font_dict['&#' + str(c.attrib['code'])[1:] + ';'] = str(g_dict[c.attrib['name']])
        except:
            pass
    print(font_dict)

if __name__ == '__main__':
    gouzao()
    make_parseDict()


